package rosalind.problem6

/**
 * Created by hamishdickson on 07/04/15.
 *
 * Given two strings s and t of equal length, the Hamming distance between s and t, denoted dH(s,t), is the number of
 * corresponding symbols that differ in s and t. See Figure 2.
 *
 * Given: Two DNA strings s and t of equal length (not exceeding 1 kbp).
 * Return: The Hamming distance dH(s,t).
 *
 * note: I'm aware I could have done this with a zip() function, but that's not my aim
 *
 */
class Mutations {
  def hemmingDistanceCounter(nuc1: String, nuc2: String): Int = {
    def hdc(n1: String, n2: String, count: Int): Int = {
      if (n1.length <= 0 || n2.length <=0) count
      else if (n1.substring(0, 1) == n2.substring(0, 1)) {
        if (n1.length <= 1 || n2.length <= 1) return count
        hdc(n1.substring(1), n2.substring(1), count)
      }
      else {
        if (n1.length < 1 || n2.length < 1) return count + 1
        hdc(n1.substring(1), n2.substring(1), count + 1)
      }
    }

    hdc(nuc1, nuc2, 0)
  }

  def count(text: String, pattern: String, d: Int): Int = {
    def aux(t: String, count: Int): Int = {
      if (t.length < pattern.length || t.length < 1) count
      else if (hemmingDistanceCounter(pattern, t.substring(0, pattern.length)) <= d) aux(t.substring(1), count + 1)
      else aux(t.substring(1), count)
    }

    aux(text, 0)
  }


  /**
   * OK, so there is a problem with this - it only considers patterns that are in the in genone - not all possible
   * mutations - need a generator to create those
   */
  def dNeighbourhood(text: String, k: Int, d: Int): List[String] = {
    def aux(pos: Int, max: Int, list: List[String]): List[String] = {
      if (pos + k > text.length) list
      else {
        val e = count(text, text.substring(pos, pos + k), d)
        if (e < max) aux(pos + 1, max, list)
        else if (e == max) aux(pos + 1, max, text.substring(pos, pos + k) :: list)
        else aux(pos + 1, e, List(text.substring(pos, pos + k)))
      }
    }

    aux(0, 0, List())
  }

  /**
   * first attempt - brute force it and generate everything
   *
   * even on the face of it this doesn't sound right - you only care about stuff that's within a certain distance of
   * what's in your string
   */
  def generator(k: Int): List[String] = {

  }


  def matching(text: String, pattern: String, d: Int): List[Int] = {
    def aux(pos: Int, l: List[Int]): List[Int] = {
      if (pattern.length + pos > text.length) l
      else if (hemmingDistanceCounter(text.substring(pos, pos + pattern.length), pattern) <= d) aux(pos + 1, pos :: l)
      else aux(pos + 1, l)
    }

    aux(0, Nil).reverse
  }
}

